#### 代码块
```golang
var kx, ky, xdeep, ydeep, ffather int
var ans bool

func isCousins(root *TreeNode, x int, y int) bool {
	ffather = -2
	ans = true
	kx = x
	ky = y
	ergodic(root, 0, -1)
	return ans
}

func ergodic(now *TreeNode, deep int, tmp int) {
	if now.Val == kx {
		xdeep = deep
		if ffather == -2 {
			ffather = tmp
		} else {
			if ffather == tmp || xdeep != ydeep {
				ans = false
			}
		}
	}
	if now.Val == ky {
		ydeep = deep
		if ffather == -2 {
			ffather = tmp
		} else {
			if ffather == tmp || xdeep != ydeep {
				ans = false
			}
		}
	}
	if now.Left != nil {
		ergodic(now.Left, deep+1, now.Val)
	}
	if now.Right != nil {
		ergodic(now.Right, deep+1, now.Val)
	}
	return
}
```
